mapper.select:
select s_shop_car.id, s_shop_car.client_id, s_shop_car.product_id, s_shop_car.good_id, s_shop_car.good_count, s_shop_car.update_time, s_product.name productName,s_product.small_pic productImage,s_product.shops shopId, s_product.shelf_status shelfStatus from s_shop_car,s_product WHERE s_shop_car.product_id = s_product.id AND s_product.shelf_status = 1 AND client_id = #{clientId}
#{shopId}
ORDER BY s_shop_car.update_time DESC

select s_shop_car.id, s_shop_car.client_id, s_shop_car.product_id, s_shop_car.good_id, s_shop_car.good_count, s_shop_car.update_time, s_product.name productName,s_product.small_pic productImage,s_product.shops shopId,s_product.shelf_status shelfStatus from s_shop_car,s_product WHERE s_shop_car.product_id = s_product.id AND client_id = #{clientId} AND s_product.shelf_status = 1
#{shopCarId}
ORDER BY s_shop_car.update_time DESC
