mapper.update:
UPDATE
s_spike_product
SET
stock_count = #{stockCount},
update_version = #{newVersionNo}
WHERE id = #{id}
AND update_version = #{oldVersionNo}

mapper.select:
SELECT

FROM
s_spike_product
WHERE s_spike_product.delete_status = 0

  AND date_format(s_spike_product.start_time, '%Y-%m-%d %H:%i:%s') <= #{startTime}
  AND date_format(s_spike_product.end_time) > #{startTime}


  AND date_format(s_spike_product.start_time, '%Y-%m-%d %H:%i:%s') < #{endTime}
  AND date_format(s_spike_product.end_time) >= #{endTime}


  AND s_spike_product.product_id = #{productId}


  AND s_spike_product.good_id = #{goodId}

LIMIT 1
